import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-content',
  templateUrl: './content.component.html',
  styleUrls: ['./content.component.scss']
})
export class ContentComponent {
  @Input() photo      : string = '';
  @Input() title      : string = '';
  @Input() description: string = '';
}
